#
# @lc app=leetcode id=15 lang=python3
#
# [15] 3Sum
#

# @lc code=start


class Solution:
  def threeSum(self, nums):
    nums.sort()
    ans = set()
    for ia in range(len(nums)):
      m = set()
      for ib in range(ia+1, len(nums)):
        a = nums[ia]
        b = nums[ib]
        rest = 0 - a - b
        if rest in m:
          ans.add((a, rest, b))
        m.add(b)
    return ans


